import { Todo } from "../model/todo.model";

export const addLeadingZero = (text: string) => {
  return `0${text}`.slice(-2);
};

export const stringifyDate = (date: Date) => {
  const dateString = `${date.getDate()}/${
    date.getMonth() + 1
  }/${date.getFullYear()}`;
  return dateString;
};

export const groupTodosByDate = (todos: Todo[]) => {
  console.log(todos);
  todos.reduce((group: any, todo: Todo) => {
    console.log(todo);
    const { date } = todo;
    group[date] = group[date] ?? [];
    group[date].push(todo);
    console.log("something");
    //returns an object with dates as properties
    return group;
  }, {});
};
