import React, { useState } from "react";
import { act } from "react-dom/test-utils";

import months from "../../data/months";

const sameDate = (date1: Date, date2: Date) =>
  date1.getDate() === date2.getDate() &&
  date1.getMonth() === date2.getMonth() &&
  date1.getFullYear() === date2.getFullYear();

type ChangeMonthProps = {};

type Day = {
  dayClass: string;
  number: number | null;
};

const ChangeMonth: React.FC<ChangeMonthProps> = () => {
  let firstOfMonth = new Date();
  firstOfMonth.setDate(1);
  const [actualDate, setActualDate] = useState(firstOfMonth);

  //kalendar je niz dana kao objekti sa klasama i brojem koji je broj dana
  const calendar: Day[] = [];

  const onDecrease = () => {
    if (actualDate.getMonth() === 0) {
      setActualDate((prevDate) => {
        const newDate = new Date(prevDate.getTime());
        newDate.setMonth(11);
        newDate.setFullYear(prevDate.getFullYear() - 1);
        return newDate;
      });
    } else {
      setActualDate((prevDate) => {
        const newDate = new Date(prevDate.getTime());
        newDate.setMonth(prevDate.getMonth() - 1);
        return newDate;
      });
    }
  };

  const onIncrease = () => {
    if (actualDate.getMonth() === 11) {
      setActualDate((prevDate) => {
        const newDate = new Date(prevDate.getTime());
        newDate.setMonth(0);
        newDate.setFullYear(prevDate.getFullYear() + 1);
        return newDate;
      });
    } else {
      setActualDate((prevDate) => {
        const newDate = new Date(prevDate.getTime());
        newDate.setMonth(prevDate.getMonth() + 1);
        return newDate;
      });
    }
  };

  let thisMonth = new Date(actualDate.getTime());
  thisMonth.setDate(1);

  //zbog stampanja stavimo prvo dane koji ne pocinju od pocetka nedelje

  while(thisMonth.getMonth() === actualDate.getMonth()) {

    //napravimo dayClass kao niz u koji stavljamo klase
    let dayClass = ["day"];

    //ako je actualDAte == today stavimo today
    if (sameDate(actualDate, new Date())) {
      dayClass.push("today");
    }
    //zatim filter todos za taj datum koji prolazimo actual date koj pocinje od prvog

    //zatim niz klasa stavimo u string i dodamo u kalendar niz objekat sa stringom i broejm
    calendar.push({
      dayClass: dayClass.join(" "),
      number: thisMonth.getDate(),
    });

    //uvecamo dan za jedan ali mesec mora biti isti
    thisMonth.setDate(thisMonth.getDate() + 1);
  }
  return (
    <>
      <div>
        <button onClick={onDecrease}> decrement - </button>
        <p>{`${months[actualDate.getMonth()]} ${actualDate.getFullYear()}`}</p>
        <button onClick={onIncrease}> increment + </button>
      </div>
      <div>
          {calendar.map((day, index) => {
              return <span className={day.dayClass}>{day.number}</span>
          })}
      </div>
    </>
  );
};

export default ChangeMonth;
