import React, { ChangeEvent, DragEvent } from "react";
import { ProjectStatus, Todo } from "../model/todo.model";
import { filterTodos } from "../util/util-functions";

import "./TodoList.css";

interface TodoListProps {
  list: string;
  items: Todo[];
  onToggleTodoStatus: (
    id: string,
    newStatus: ProjectStatus,
    event: ChangeEvent
  ) => void;
  onDeleteTodo: (id: string) => void;
  onEditableTodo: (id: string) => void;
}

const TodoList: React.FC<TodoListProps> = ({
  list,
  items,
  onToggleTodoStatus,
  onDeleteTodo,
  onEditableTodo,
}) => {
  const dragStartHandler = (
    event: DragEvent<HTMLLIElement>,
    todoId: string
  ) => {
    event.dataTransfer.setData("todo_id", todoId);
    event.dataTransfer.effectAllowed = "move";
  };

  let newItems: Todo[] = [];

  if (list === "active") {
    newItems = filterTodos(items, ProjectStatus.Active);
  } else {
    newItems = filterTodos(items, ProjectStatus.Finished);
  }

  return (
    <ul>
      {newItems.map((todo) => {
        return !todo.isEditable ? (
          <li
            draggable="true"
            key={todo.id}
            className={`todo ${
              todo.status === 0 ? "active-todo" : "finished-todo"
            }`}
            onDragStart={(event) => dragStartHandler(event, todo.id)}
          >
            <div className="todo-primary-info">
              <div>
                <input
                  checked={todo.status === 0 ? false : true}
                  type="checkbox"
                  className={todo.priority}
                  onChange={(event) => {
                    onToggleTodoStatus(todo.id, todo.status, event);
                  }}
                />
                <span className="draggable">&#8285;&#8285;</span>
                <div>{todo.text}</div>
              </div>
              <div>
                <button
                  className="edit__btn"
                  onClick={onEditableTodo.bind(null, todo.id)}
                >
                  <span>&#9998;</span>
                </button>
                <button
                  className="delete__btn"
                  onClick={onDeleteTodo.bind(null, todo.id)}
                >
                  <span>&#10006;</span>
                </button>
              </div>
            </div>
            <div className="todo-secondary-info">
              <span>{todo.date}</span>
            </div>
          </li>
        ) : (
          <li key={todo.id}>
            <form>
              <div>
                <input
                  type="text"
                  id="todo-text"
                  placeholder="Title"
                  required
                />
                <input
                  type="date"
                  id="todo-date"
                  min={new Date().toISOString().split("T")[0]}
                  required
                />
                <select id="todo-priority">
                  <option value="p1">Priority 1</option>
                  <option value="p2">Priority 2</option>
                  <option value="p3">Priority 3</option>
                  <option value="p4">Priority 4</option>
                </select>
              </div>
              <button type="submit">&#43;</button>
            </form>
          </li>
        );
      })}
    </ul>
  );
};

export default TodoList;
