import React, { useState } from "react";

import TodoList from "./components/TodoList";
import NewTodo from "./components/NewTodo";

import { ProjectStatus, Todo } from "./model/todo.model";
import TodoListLayout from "./ui/TodoListLayout";

const App: React.FC = () => {
  const [todos, setTodos] = useState<Todo[]>([]);
  const [activeTodos, setActiveTodos] = useState<Todo[]>([]);
  const [finishedTodos, setFinishedTodos] = useState<Todo[]>([]);

  setActiveTodos(todos.filter((item) => item.status === ProjectStatus.Active));
  setFinishedTodos(
    todos.filter((item) => item.status === ProjectStatus.Finished)
  );

  const todoAddHandler = (text: string, date: Date) => {
    setTodos((prevTodos) => {
      return [
        ...prevTodos,
        {
          id: Math.random().toString(),
          text: text,
          status: ProjectStatus.Active,
          date: {
            day: date.getDate(),
            month: date.getMonth() + 1,
            year: date.getFullYear(),
            fullDate: date,
          },
        },
      ];
    });
  };

  const todoDeleteHandler = (todoId: string) => {
    setTodos((prevTodos) => prevTodos.filter((todo) => todo.id !== todoId));
  };

  const toggleTodoStatus = (todoId: string, status: ProjectStatus) => {
    const todoIndex = todos.findIndex((todo) => todo.id === todoId);
    const newTodos = [...todos];
    const newTodo: Todo = todos[todoIndex];
    status === ProjectStatus.Active
      ? (newTodo.status = ProjectStatus.Finished)
      : (newTodo.status = ProjectStatus.Active);
    newTodos[todoIndex] = newTodo;
    setTodos(newTodos);
  };

  const changeTodoState = (
    todoId: string,
    layoutClass: string,
    event?: Event
  ) => {
    console.log(event);
    const todoIndex = todos.findIndex((todo) => todo.id === todoId);
    const newTodos = [...todos];
    const newTodo: Todo = todos[todoIndex];
    const status = layoutClass.split("-")[1];
    status === "active"
      ? (newTodo.status = ProjectStatus.Active)
      : (newTodo.status = ProjectStatus.Finished);
    newTodos[todoIndex] = newTodo;
    setTodos(newTodos);
  };

  const insertTodoOnNewPosition = (index: number) => {
    const newTodos = [...todos];
    const todo = newTodos[index];
    newTodos.splice(index, 1);
    newTodos.splice(index, 0, todo);
    setTodos(newTodos);
  };

  return (
    <>
      <div className="App">
        <NewTodo onAddTodo={todoAddHandler} />
        <TodoListLayout type={"active"} changeTodoState={changeTodoState}>
          <TodoList
            items={activeTodos}
            onChangeTodoStatus={toggleTodoStatus}
            onDeleteTodo={todoDeleteHandler}
            insertTodoOnNewPosition={insertTodoOnNewPosition}
          />
        </TodoListLayout>
        <TodoListLayout type={"finished"} changeTodoState={changeTodoState}>
          <TodoList
            items={finishedTodos}
            onChangeTodoStatus={toggleTodoStatus}
            onDeleteTodo={todoDeleteHandler}
            insertTodoOnNewPosition={insertTodoOnNewPosition}
          />
        </TodoListLayout>
      </div>
    </>
  );
};

export default App;
