import React, { useRef } from "react";

import "./NewTodo.css";

interface TodoAddProps {
  onAddTodo: (text: string, date: Date, priority: string) => void;
}

const NewTodo: React.FC<TodoAddProps> = (props) => {
  const todoTextRef = useRef<HTMLInputElement>(null);
  const todoDateRef = useRef<HTMLInputElement>(null);
  const todoPriorityRef = useRef<HTMLSelectElement>(null);

  const todoSubmitHandler = (event: React.FormEvent) => {
    event.preventDefault();
    const enteredText = todoTextRef.current!.value;
    const enteredDate = new Date(todoDateRef.current!.value);
    const enteredPriority = todoPriorityRef.current!.value;
    props.onAddTodo(enteredText, enteredDate, enteredPriority);
    todoTextRef.current!.value = "";
  };

  return (
    <form onSubmit={todoSubmitHandler}>
      <div>
        <input type="text" id="todo-text" ref={todoTextRef} placeholder="Title" required />
        <input
          type="date"
          id="todo-date"
          ref={todoDateRef}
          min={new Date().toISOString().split("T")[0]}
          required
        />
        <select id="todo-priority" ref={todoPriorityRef}>
          <option value="p1">Priority 1</option>
          <option value="p2">Priority 2</option>
          <option value="p3">Priority 3</option>
          <option value="p4">Priority 4</option>
        </select>
      </div>
      <button type="submit">&#43;</button>
    </form>
  );
};

export default NewTodo;
