import React, { useRef } from "react";
import { useClickOutside } from "../hooks/useClickOutside";

import { Todo } from "../model/todo.model";

import "./NewTodo.css";

interface TodoAddProps {
  type: "new" | "edit";
  submitTodo: (
    text: string,
    date: Date,
    priority: string,
    todoId?: string
  ) => void;
  todo?: Todo;
  onEditableTodo?: (id: string) => void;
}

const NewTodo: React.FC<TodoAddProps> = (props) => {
  const todoTextRef = useRef<HTMLInputElement>(null);
  const todoDateRef = useRef<HTMLInputElement>(null);
  const todoPriorityRef = useRef<HTMLSelectElement>(null);

  const todoSubmitHandler = (event: React.FormEvent) => {
    event.preventDefault();
    const enteredText = todoTextRef.current!.value;
    const enteredDate = new Date(todoDateRef.current!.value);
    const enteredPriority = todoPriorityRef.current!.value;
    if (props.type === "new") {
      props.submitTodo(enteredText, enteredDate, enteredPriority);
    } else {
      props.submitTodo(
        enteredText,
        enteredDate,
        enteredPriority,
        props.todo!.id
      );
    }
    todoTextRef.current!.value = "";
  };

  const testClick = () => {
    console.log("test click from new todo");
  };

  const handler = (_event: MouseEvent): void => {
    console.log("ja sam funckija koja se izvrsava");
  };
  const formRef = useRef(null);
  console.log(formRef);
  useClickOutside(formRef, handler);

  return (
    <form onSubmit={todoSubmitHandler}>
      <div ref={formRef}>
        <input
          type="text"
          id="todo-text"
          ref={todoTextRef}
          placeholder="Title"
          defaultValue={props.type === "edit" ? props.todo!.text : ""}
          required
        />
        <input
          type="date"
          id="todo-date"
          ref={todoDateRef}
          min={new Date().toISOString().split("T")[0]}
          defaultValue={
            props.type === "edit"
              ? new Date(props.todo!.fullDate).toISOString().substring(0, 10)
              : new Date().toISOString().substring(0, 10)
          }
          required
        />
        <select
          id="todo-priority"
          ref={todoPriorityRef}
          defaultValue={props.type === "edit" ? props.todo!.priority : "p1"}
        >
          <option value="p1">Priority 1</option>
          <option value="p2">Priority 2</option>
          <option value="p3">Priority 3</option>
          <option value="p4">Priority 4</option>
        </select>
      </div>
      <button type="submit">&#43;</button>
      <button
        type="button"
        onClick={() =>
          props.type === "edit"
            ? props.onEditableTodo!(props.todo!.id)
            : testClick()
        }
      >
        X
      </button>
    </form>
  );
};

export default NewTodo;
