import React, { useState } from "react";

import months from "../../data/months";

type ChangeMonthProps = {};

const ChangeMonth: React.FC<ChangeMonthProps> = () => {
  const monthNow = new Date().getMonth() + 1;
  const yearNow = new Date().getFullYear();

  const [currentMonth, setCurrentMonth] = useState(monthNow);
  const [currentYear, setCurrentYear] = useState(yearNow);

  const onDecrease = () => {
    if (currentMonth === 0) {
      setCurrentMonth((prevState) => (prevState = 11));
    } else {
      setCurrentMonth((prevState) => prevState - 1);
    }
  };

  const onIncrease = () => {
    if (currentMonth === 11) {
      setCurrentMonth((prevState) => (prevState = 0));
    } else {
      setCurrentMonth((prevState) => prevState + 1);
    }
  };

  console.log("current month", currentMonth);

  return (
    <div>
      <button onClick={onDecrease}> decrement - </button>
      <p>{`${months[currentMonth]} ${currentYear}`}</p>
      <button onClick={onIncrease}> increment + </button>
    </div>
  );
};

export default ChangeMonth;
