import React, { useRef } from "react";

interface TodoAddProps {
    onAddTodo: (text: string) => void
}

const NewTodo: React.FC<TodoAddProps> = props => {
  const todoTextRef = useRef<HTMLInputElement>(null);
  const todoDateRef = useRef<HTMLInputElement>(null);

  const todoSubmitHandler = (event: React.FormEvent) => {
    event.preventDefault();
    const enteredText = todoTextRef.current!.value;
    props.onAddTodo(enteredText);
    todoTextRef.current!.value = "";
  };

  
  const date = new Date();
  const today = [date.getFullYear(), date.getMonth(), date.getDay()].join("-");

  console.log(today);

  return (
    <form onSubmit={todoSubmitHandler}>
      <div>
        <label htmlFor="todo-text">Todo text</label>
        <input type="text" id="todo-text" ref={todoTextRef}/>
      </div>
      <div>
          <label htmlFor="todo-date">When to do it?</label>
          <input type="date" ref={todoDateRef} min={"2022-02-22"}/>
      </div>
      <button type="submit">Add todo</button>
    </form>
  );
};

export default NewTodo;
