import React, { DragEvent } from "react";
import { ProjectStatus } from "../model/todo.model";

import "./TodoList.css";

interface TodoListProps {
  items: { id: string; text: string; status: ProjectStatus }[];
  onChangeTodoStatus: (id: string, newStatus: ProjectStatus) => void;
  onDeleteTodo: (id: string) => void;
  
}

const TodoList: React.FC<TodoListProps> = (props) => {
  const dragStartHandler = (
    event: DragEvent<HTMLLIElement>,
    todoId: string
  ) => {
    event.dataTransfer.setData("todo_id", todoId);
    event.dataTransfer.effectAllowed = "move";
  };

  const insertTodoOnNewPosition = (index: number) => {
    const newList = [...props.items];
    newList.splice(index, 1);
    newList.splice(index, 0, index);
    props.items = newList;
  }

  const dragEnterHandler = (event: DragEvent, index: number) => {
      (event.currentTarget as HTMLLIElement).style.backgroundColor = "blue";
      insertTodoOnNewPosition(index);
  }

  const dragLeaveHandler = (event: DragEvent) => {
    (event.currentTarget as HTMLLIElement).style.backgroundColor = "";
  }

  const dropHandler = (event: DragEvent) => {

  }

  return (
    <ul>
      {props.items.map((todo, index) => (
        <li
          draggable="true"
          key={todo.id}
          className={`todo ${
            todo.status === 0 ? "active-todo" : "finished-todo"
          }`}
          onDragStart={(event) => dragStartHandler(event, todo.id)}
          onDragEnter={(event) => dragEnterHandler(event, index)}
          onDragLeave={(event) => dragLeaveHandler(event)}
          onDrop={(event) => dropHandler(event)}
        >
          <span>
            <span className="draggable">&#8285;&#8285;</span>
            {todo.text}
          </span>
          <div>
            <button
              onClick={props.onChangeTodoStatus.bind(
                null,
                todo.id,
                todo.status
              )}
            >
              &#9998;
            </button>
            <button onClick={props.onDeleteTodo.bind(null, todo.id)}>
              &#10006;
            </button>
          </div>
        </li>
      ))}
    </ul>
  );
};

export default TodoList;
