import React, { ChangeEvent } from "react";
import FullCalendar from "@fullcalendar/react";
import dayGridPlugin from "@fullcalendar/daygrid";

import "./SearchBar.css";

interface SearchBarProps {
  onSearch: (event: ChangeEvent<HTMLInputElement>) => void;
  onShowTodaysTodos: () => void;
  onShowAllTodos: () => void;
  searchText: string;
}

const SearchBar: React.FC<SearchBarProps> = ({
  onSearch,
  onShowTodaysTodos,
  onShowAllTodos,
  searchText,
}) => {
  return (
    <>
      <div className="search-bar">
        <button onClick={onShowAllTodos}>ALL</button>
        <button onClick={onShowTodaysTodos}>TODAY</button>
        <input
          type="search"
          onChange={(event) => {
            onSearch(event);
          }}
          value={searchText}
        />
      </div>
      <FullCalendar
        plugins={[dayGridPlugin]}
        initialView="dayGridMonth"
        headerToolbar={{
          start: "prev,next today",
          center: "dayGridMonth,timeGridWeek,timeGridDay",
          end: "new",
        }}
        customButtons={{
          new: {
            text: "Add todo",
            click: () => console.log("something"),
          },
        }}
        events={[
          { id: "1", title: "todo 1", start: "2022-03-01", end: "2022-03-01" },
          { id: "2", title: "todo 2", start: "2022-03-01", end: "2022-03-01" },
          { id: "3", title: "todo 3", start: "2022-03-02", end: "2022-03-02" },
          { id: "4", title: "todo 4", start: "2022-03-02", end: "2022-03-03" },
        ]}
        eventClick={(e) => {console.log(e.event.title)}}
      />
    </>
  );
};

export default SearchBar;
