import React, { useState } from "react";

import months from "../../data/months";

type ChangeMonthProps = {};

const ChangeMonth: React.FC<ChangeMonthProps> = () => {
  const [actualDate, setActualDate] = useState(new Date());

  actualDate.setDate(1);
  

  const calendar = [];

  const onDecrease = () => {
    if (actualDate.getMonth() === 0) {
      setActualDate((prevDate) => {
        const newDate = new Date(prevDate.getTime());
        newDate.setMonth(11);
        newDate.setFullYear(prevDate.getFullYear() - 1);
        return newDate;
      });
    } else {
      setActualDate((prevDate) => {
        const newDate = new Date(prevDate.getTime());
        newDate.setMonth(prevDate.getMonth() - 1);
        return newDate;
      });
    }
  };

  const onIncrease = () => {
    if (actualDate.getMonth() === 11) {
      setActualDate((prevDate) => {
        const newDate = new Date(prevDate.getTime());
        newDate.setMonth(0);
        newDate.setFullYear(prevDate.getFullYear() + 1);
        return newDate;
      });
    } else {
      setActualDate((prevDate) => {
        const newDate = new Date(prevDate.getTime());
        newDate.setMonth(prevDate.getMonth() + 1);
        return newDate;
      });
    }
  };

  let actualMonth = actualDate.getMonth();

  while(actualDate.getMonth() === actualMonth) {
      
    //napravimo dayClass kao niz u koji stavljamo klase
    //ako je actualDAte == today stavimo today
    //zatim filter todos za taj datum koji prolazimo actual date koj pocinje od prvog
  }

  return (
    <>
      <div>
        <button onClick={onDecrease}> decrement - </button>
        <p>{`${months[actualDate.getMonth()]} ${actualDate.getFullYear()}`}</p>
        <button onClick={onIncrease}> increment + </button>
      </div>
      <div>

      </div>
    </>
  );
};

export default ChangeMonth;
