import React, {DragEvent} from "react";

import "./TodoListLayout.css";

interface ListLayoutProps {
  children: {
    props: object;
  };
  type: "active" | "finished";
  dragOverHandler: (event: DragEvent) => void;
  dropHandler: (event: DragEvent) => void;
  dragLeaveHandler: (event: DragEvent) => void;
  insideDroppableTarget: boolean;
}

const TodoListLayout: React.FC<ListLayoutProps> = ({ type, children, dragOverHandler, dropHandler, dragLeaveHandler, insideDroppableTarget }) => {
  const isActive = type === "active";

  const contentToRender =
    children.props.items.length === 0 ? (
      isActive ? (
        <p>Start being productive! Add some todos.</p>
      ) : (
        <p>Well done! You completed all your tasks.</p>
      )
    ) : (
      children
    );

  return (
    <div
      className={`todos-layout ${insideDroppableTarget ? "drag-target-active" : ""}`}
      onDragOver={(event) => dragOverHandler(event)}
      onDrop={(event) => dropHandler(event)}
      onDragLeave={(event) => dragLeaveHandler(event)}
    >
      <h2 className={isActive ? "active-layout-heading" : "finished-layout-heading"}>
        {isActive ? "ACTIVE TODOS" : "FINISHED TODOS"}
      </h2>
      {contentToRender}
    </div>
  );
};

export default TodoListLayout;
