import React, { useState } from "react";

import TodoList from "./components/TodoList";
import NewTodo from "./components/NewTodo";

import { ProjectStatus, Todo } from "./model/todo.model";
import TodoListLayout from "./ui/TodoListLayout";

const App: React.FC = () => {
  const [todos, setTodos] = useState<Todo[]>([]);
  const activeTodos = todos.filter(item => item.status === ProjectStatus.Active);
  const finishedTodos = todos.filter(item => item.status === ProjectStatus.Finished);

  const todoAddHandler = (text: string) => {
    setTodos((prevTodos) => {
      return [
        ...prevTodos,
        {
          id: Math.random().toString(),
          text: text,
          status: ProjectStatus.Active,
        },
      ];
    });
    console.log(text);
  };

  const todoDeleteHandler = (todoId: string) => {
    setTodos((prevTodos) => prevTodos.filter((todo) => todo.id !== todoId));
  };

  console.table(todos);

  return (
    <>
      <div className="App">
        <NewTodo onAddTodo={todoAddHandler} />
        <TodoList items={todos} onDeleteTodo={todoDeleteHandler} />
        <TodoListLayout type={"active"}>
          <TodoList items={activeTodos} onDeleteTodo={todoDeleteHandler} />
        </TodoListLayout>
        <TodoListLayout type={"finished"}>
          <TodoList items={finishedTodos} onDeleteTodo={todoDeleteHandler} />
        </TodoListLayout>
      </div>
    </>
  );
};

export default App;
