import { Todo, ProjectStatus } from "../model/todo.model";

export const addLeadingZero = (number: number) => {
  return `0${number}`.slice(-2);
};

export const stringifyDate = (date: Date) => {
  const dateString = `${addLeadingZero(date.getDate())}/${addLeadingZero(
    date.getMonth() + 1
  )}/${date.getFullYear()}`;
  return dateString;
};

export const groupTodosByDate = (todos: Todo[]): {} => {
  return todos.reduce((group: any, todo: Todo) => {
    const { date } = todo;
    group[date] = group[date] ?? [];
    group[date].push(todo);

    //returns an object with dates as properties
    return group;
  }, {});
};

export const filterActiveTodos = (todos: Todo[]) => {
  return todos.filter((todo) => todo.status === ProjectStatus.Active);
};

export const filterFinishedTodos = (todos: Todo[]) => {
  return todos.filter((todo) => todo.status === ProjectStatus.Finished);
};
