import React, { ChangeEvent } from "react";

import "@mobiscroll/react/dist/css/mobiscroll.min.css";
import { Datepicker } from "@mobiscroll/react";

import "./SearchBar.css";

interface SearchBarProps {
  onSearch: (event: ChangeEvent<HTMLInputElement>) => void;
  onShowTodaysTodos: () => void;
  onShowAllTodos: () => void;
  searchText: string;
}

const SearchBar: React.FC<SearchBarProps> = ({
  onSearch,
  onShowTodaysTodos,
  onShowAllTodos,
  searchText,
}) => {
  return (
    <div className="search-bar">
      <button onClick={onShowAllTodos}>ALL</button>
      <button onClick={onShowTodaysTodos}>TODAY</button>
      <input
        type="search"
        onChange={(event) => {
          onSearch(event);
        }}
        value={searchText}
      />

      <Datepicker
        theme="windows"
        themeVariant="dark"
        controls={["calendar"]}
        display="inline"
        select="preset-range"
        firstSelectDay={1}
        selectSize={14}
        calendarType="week"
        calendarSize={1}
      />
    </div>
  );
};

export default SearchBar;
