import React from "react";

import "./TodoListLayout.css";

interface ListLayoutProps {
  children: {
    props: object;
  };
  type: "active" | "finished";
}

const TodoListLayout: React.FC<ListLayoutProps> = ({ type, children }) => {
  const isActive = type === "active";

  const contentToRender =
    children.props.items.length === 0 ? (
      isActive ? (
        <p>Start being productive! Add some todos.</p>
      ) : (
        <p>Well done! You completed all your tasks.</p>
      )
    ) : (
      children
    );

  const dragOverChengeStyleHandler = () => {
    return "drag-target-active";
  };

  const dragLeaveChangeStyleHandler = () => {
    return "";
  };

  return (
    <div className={`todos-layout ${dragOverChengeStyleHandler} ${dragLeaveChangeStyleHandler}`}>
      <h2 className={isActive ? "active-layout" : "finished-layout"}>
        {isActive ? "ACTIVE TODOS" : "FINISHED TODOS"}
      </h2>
      {contentToRender}
    </div>
  );
};

export default TodoListLayout;
