import React, { DragEvent } from "react";
import { Draggable } from "../model/drag-drop";
import { ProjectStatus } from "../model/todo.model";

import "./TodoList.css";

interface TodoListProps {
  items: { id: string; text: string; status: ProjectStatus }[];
  onChangeTodoStatus: (id: string, newStatus: ProjectStatus) => void;
  onDeleteTodo: (id: string) => void;
}

type Props = TodoListProps & Draggable;

const TodoList: React.FC<Props> = (props) => {
  const dragStartHandler = (event: DragEvent<HTMLLIElement>, todoId: string) => {
    console.log(event);
    console.log(event.dataTransfer);
  };

  const dropHandler = (event: DragEvent<HTMLLIElement>) => {

  }

  return (
    <ul onDrop={(event) => dropHandler(event)}>
      {props.items.map((todo) => (
        <li
          draggable="true"
          key={todo.id}
          className={`todo ${todo.status === 0 ? "active-todo" : "finished-todo"}`}
          onDragStart={(event) => dragStartHandler(event, todo.id)}
        >
          <span>{todo.text}</span>
          <div>
            <button
              onClick={props.onChangeTodoStatus.bind(
                null,
                todo.id,
                todo.status
              )}
            >
              &#9998;
            </button>
            <button onClick={props.onDeleteTodo.bind(null, todo.id)}>
              &#10006;
            </button>
          </div>
        </li>
      ))}
    </ul>
  );
};

export default TodoList;
