import React, { FormEvent, useEffect, useState } from "react";

import TodoList from "./components/TodoList";
import NewTodo from "./components/NewTodo";
import SearchBar from "./components/SearchBar/SearchBar";

import { ProjectStatus, Todo } from "./model/todo.model";

import TodoListLayout from "./ui/TodoListLayout";

import {
  stringifyDate,
  groupTodosByDate,
  filterActiveTodos,
  filterFinishedTodos,
} from "./util/util-functions";

const App: React.FC = () => {
  const [searchText, setSearchText] = useState<string>("");
  const [todos, setTodos] = useState<Todo[]>(() => {
    const savedTodos = localStorage.getItem("todos");
    let initialTodos: Todo[] = [];
    if (savedTodos) {
      initialTodos = JSON.parse(savedTodos);
    }
    return initialTodos;
  });


  let activeTodos = filterActiveTodos(todos);
  let finishedTodos = filterFinishedTodos(todos);

  const todoAddHandler = (text: string, date: Date, priority: string) => {
    setTodos((prevTodos) => {
      return [
        ...prevTodos,
        {
          id: Math.floor(Math.random() * (100000 - 10000) + 10000).toString(),
          text: text,
          status: ProjectStatus.Active,
          priority: priority,
          date: stringifyDate(date),
          createdOn: new Date(),
        },
      ];
    });
  };

  const todoDeleteHandler = (todoId: string) => {
    setTodos((prevTodos) => prevTodos.filter((todo) => todo.id !== todoId));
  };

  const toggleTodoStatus = (todoId: string, status: ProjectStatus) => {
    // const todoIndex = todos.findIndex((todo) => todo.id === todoId);
    // const newTodos = [...todos];
    // const newTodo: Todo = todos[todoIndex];
    // status === ProjectStatus.Active
    //   ? (newTodo.status = ProjectStatus.Finished)
    //   : (newTodo.status = ProjectStatus.Active);
    // newTodos[todoIndex] = newTodo;
    // setTodos(newTodos);

    const newStatus =
      status === ProjectStatus.Active
        ? ProjectStatus.Finished
        : ProjectStatus.Active;
    setTodos((prevTodos) => {
      return prevTodos.map((todo) => {
        return todo.id === todoId ? { ...todo, status: newStatus } : todo;
      });
    });
  };

  // THIS IS FOR DRAG
  const changeTodoState = (todoId: string, status: ProjectStatus) => {
    const todoIndex = todos.findIndex((todo) => todo.id === todoId);
    const newTodos = [...todos];
    const newTodo: Todo = todos[todoIndex];
    status !== ProjectStatus.Active
      ? (newTodo.status = ProjectStatus.Finished)
      : (newTodo.status = ProjectStatus.Active);
    newTodos[todoIndex] = newTodo;
    setTodos(newTodos);
  };

  const searchHandler = (event: FormEvent<HTMLFormElement>) => {
    setSearchText(event.target.value);

    const grouped: {
      [prop: string]: Todo[];
    } = groupTodosByDate(todos);
    const today: string = event.target.value;
    if (grouped[today]) {
      const todaysTodos: Todo[] = grouped[today];
    } 
  };

  const showAllTodosHandler = () => {
  };

  useEffect(() => {
    localStorage.setItem("todos", JSON.stringify(todos));
  }, [todos]);

  return (
    <>
      <div className="App">
        <NewTodo onAddTodo={todoAddHandler} />
        <SearchBar
          onSearch={searchHandler}
          onShowTodaysTodos={showTodaysTodosHandler}
          onShowAllTodos={showAllTodosHandler}
          searchText={searchText}
        />
        <TodoListLayout type={"active"} changeTodoStateDrag={changeTodoState}>
          <TodoList
            items={activeTodos}
            onChangeTodoStatus={toggleTodoStatus}
            onDeleteTodo={todoDeleteHandler}
          />
        </TodoListLayout>
        <TodoListLayout type={"finished"} changeTodoStateDrag={changeTodoState}>
          <TodoList
            items={finishedTodos}
            onChangeTodoStatus={toggleTodoStatus}
            onDeleteTodo={todoDeleteHandler}
          />
        </TodoListLayout>
      </div>
    </>
  );
};

export default App;
