import React, { useState } from "react";

import TodoList from "./components/TodoList";
import NewTodo from "./components/NewTodo";

import { ProjectStatus, Todo } from "./model/todo.model";

const App: React.FC = () => {
  const [todos, setTodos] = useState<Todo[]>([]);

  const todoAddHandler = (text: string) => {
    setTodos((prevTodos) => {
      return [
        ...prevTodos,
        {
          id: Math.random().toString(),
          text: text,
          status: ProjectStatus.Active,
        },
      ];
    });
    console.log(text);
  };

  const todoDeleteHandler = (todoId: string) => {
    setTodos((prevTodos) => prevTodos.filter((todo) => todo.id !== todoId));
  };

  console.table(todos);

  return (
    <>
      <div className="App">
        <NewTodo onAddTodo={todoAddHandler} />
        <TodoList items={todos} onDeleteTodo={todoDeleteHandler} />
      </div>
      <div className="box-container">
        <div className="box l-100">
          <div className="inside l-100">inside</div>
          <div className="inside l-200">inside</div>
          <div className="inside l-300">inside</div>
          <div className="inside l-400">inside</div>
        </div>
        <div className="box l-400">
          <div className="inside l-100">inside</div>
          <div className="inside l-200">inside</div>
          <div className="inside l-300">inside</div>
          <div className="inside l-400">inside</div>
        </div>
      </div>
    </>
  );
};

export default App;
