export const todayString = (): string => {
  const date = new Date();
  const today = [
    date.getFullYear(),
    ("0" + (date.getMonth() + 1)).slice(-2),
    ("0" + date.getDate()).slice(-2),
  ].join("-");
  return today;
};

export const sliceDayName = (dayName: string) => {
  return dayName.slice(0, 3);
}
