import React, { useState } from "react";

import months from "../../data/months";

type ChangeMonthProps = {};

const ChangeMonth: React.FC<ChangeMonthProps> = () => {

  const [actualDate, setActualDate] = useState(new Date());

  const calendar = [];

  

  const onDecrease = () => {
    if (actualDate.getMonth() === 0) {
        setActualDate(prevDate => {
            prevDate.setMonth(11);
            prevDate.setFullYear(prevDate.getFullYear() - 1);
        });
    } else {
      setCurrentMonth((prevMonth) => prevMonth - 1);
    }
  };

  const onIncrease = () => {
    if (currentMonth === 11) {
      setCurrentMonth((prevMonth) => (prevMonth = 0));
      setCurrentYear((prevYear) => prevYear + 1);
    } else {
      setCurrentMonth((prevMonth) => prevMonth + 1);
    }
  };

  return (
    <>
      <div>
        <button onClick={onDecrease}> decrement - </button>
        <p>{`${months[currentMonth]} ${currentYear}`}</p>
        <button onClick={onIncrease}> increment + </button>
      </div>
      <div></div>
    </>
  );
};

export default ChangeMonth;
